(function ($) {
  'use strict';
  $(function(){
    $('html > head').append($('<style id="window-height-css"></style>'));
    $(window).resize(function(){
      $("#window-height-css").text('#capslides .windowHeight.active { height:'+$(window).height()+'px; }');
    });
    $(window).trigger('resize');


    var activeClass = 'active';
    var currentClass = 'current';
    var $window = $(window);
    var $body   = $('body.home');
    var $projects = $('#capslides');
    var $main = $('#main');
    var $slides = $('#capslides .slide');
    var $pagination = $('#capslides .pagination-list');
    var $paginationItems = $('#capslides .pagination-item');
    var currentSlideIndex = 0;
    var numSlides = $slides.length;
    var threshold = 30;
    var delta = 0;
    var isMoving = false;
    var slideDelay = 800;

    var resetDelta = function () {
      isMoving = true;
      setTimeout(function(){
        delta = 0;
        isMoving = false;
      }, slideDelay);

    };

    var showSlide = function () {
      $slides.each(function(i, slide) {
        $(slide).toggleClass(activeClass, (i >= currentSlideIndex));
        $(slide).toggleClass(currentClass, (i === currentSlideIndex));
        $paginationItems.each(function (paginationItemIndex, paginationItem) {
          $(paginationItem).toggleClass(currentClass, (paginationItemIndex === currentSlideIndex));
        });
      });
    };

    var prevSlide = function () {
      currentSlideIndex--;
      if (currentSlideIndex < 0) {
        currentSlideIndex = 0;
      }
      showSlide();
    };

    var nextSlide = function () {
      currentSlideIndex++;
      if (currentSlideIndex > numSlides) {
        currentSlideIndex = numSlides;
      }
      showSlide();
    };

    function gotoSlide (index) {
      currentSlideIndex = index;
      showSlide();
    }

    var initProjectSlides = function () {
      var buildPaginationItem = function (index, el) {
        var slideId = $(el).attr('id');
        var $newLi = $('<li class="pagination-item"></li>');

        $newLi.addClass(slideId);
        if (index === 0) { $newLi.addClass(currentClass); }
        $newLi.text("");
        $newLi.click(function(){ gotoSlide(index); });
        $pagination.append($newLi);
      };

      $slides.each(function (i,s) {
        buildPaginationItem(i, s);
      });
      buildPaginationItem(numSlides, $main);

      // Reload $paginationItems
      $paginationItems = $($paginationItems.selector);
      $window.trigger('mousewheel');
    };

    $window.mousewheel(function(e) {
      var isScollingUp = e.deltaY > 0;

      var isNotLastSlide = !(currentSlideIndex === numSlides);
      var isAtTopOfPage = $window.scrollTop() <= 0;

      if (isAtTopOfPage) {
        if (!isMoving) {
          if (isScollingUp) {
            delta--;
            if (Math.abs(delta) > threshold) {
              resetDelta();
              prevSlide();
            }
          } else {
            delta++;
            if (Math.abs(delta) > threshold) {
              resetDelta();
              nextSlide();
            }
          }
        }

        if (isNotLastSlide) {
          $body.css({'overflow': 'hidden'});
        } else {
          $body.css({'overflow': 'auto'});
        }
      } else {
        $body.css({'overflow': 'auto'});
      }


    });

    var reenteringScrollJack = false;
    var dragThreshold = 0.15;// "percentage" to drag before engaging
    var dragStart = null;  // used to determine touch / drag distance
    var percentage = 0;
    var target;
    var previousTarget;
    var windowHeight = $(window).height();
    var isAtTopOfPage;
    var isDraggingDown;

    function touchStart(event) {
      if (dragStart !== null) { return; }
      if (event.originalEvent.touches) {
        event = event.originalEvent.touches[0];
      }

      // where in the viewport was touched
      dragStart = event.clientY;
      // make sure we're dealing with a slide
      target = $slides.eq(currentSlideIndex)[0];

      // disable transitions while dragging
      if (target) {
        target.classList.add('no-animation');
      }

      previousTarget = $slides.eq(currentSlideIndex-1)[0];
      previousTarget.classList.add('no-animation');
    }

    function touchMove (event) {
      if (dragStart === null) { return; }
      if (event.originalEvent.touches) {
        event = event.originalEvent.touches[0];
      }

      delta = dragStart - event.clientY;
      percentage = delta / windowHeight;

      // Going down/next. Animate the height of the target element.
      reenteringScrollJack = false;
      if (percentage > 0) {
        target.style.height = (windowHeight-delta)+'px';;
        if (previousTarget) {
          previousTarget.style.height = '';   // reset
        }
      }

      // Going up/prev. Animate the height of the _previous_ element.
      else if (previousTarget) {
        previousTarget.style.height = (delta*-1)+'px';;
        reenteringScrollJack = true;
        if (target) {
          target.style.height = ''; // reset
        }
      }

      // Don't drag element. This is important.
      return false;
    }

    function touchEnd () {
      dragStart = null;
      if (target) {
        target.classList.remove('no-animation');
        target.style.height = ''; // reset
      }
      if (previousTarget) {
        previousTarget.classList.remove('no-animation');
        previousTarget.style.height = ''; // reset
      }

      if (percentage >= dragThreshold) {
        nextSlide();
      }
      else if ( Math.abs(percentage) >= dragThreshold ) {
        prevSlide();
      } else {
        // show current slide i.e. snap back
        showSlide();
      }

      percentage = 0;

    }

    initProjectSlides();

    $projects.on({
      'touchstart': touchStart,
      'touchmove': touchMove,
      'touchend': touchEnd
    });

    $main.on({
      'touchstart': touchStart,
      'touchmove': function (originalEvent) {
        var event;
        isAtTopOfPage = $window.scrollTop() <= 0;
        if (isAtTopOfPage) {
          if (originalEvent.originalEvent.touches) {
            event = originalEvent.originalEvent.touches[0];
          }
          isDraggingDown = (dragStart - event.clientY) < 0;
          if (isDraggingDown) {
            touchMove(originalEvent);
            if (reenteringScrollJack) {
              return false;
            }
          }
        }
      },
      'touchend': touchEnd
    });

    $(".slider-skip").click(function(e){
      e.preventDefault();
      $('#capslides .pagination-item.main').click();
    });

  });
})(jQuery);


